import React from 'react';
import './Modal.css';

const Modal = ({show, planet, hide}) => {
    if (show) {
      return(
        <div className="modal-cover">
            <div className="modal-main">
                <p> {planet} </p>
                <button className="exit-button" onClick={hideModal}></button>
            </div>
        </div>
      );
    } else {
        return(
            <div></div>
        )
    }
}

export default Modal;
