import React, { Component } from 'react';
import Searchbar from '../Components/Searchbar';
import Fixed from '../Components/Fixed';
import Content from '../Components/Content';
import './App.css'

class App extends Component {
    constructor() {
        super();
        this.state = {
            search: '',
            planetList: []
        }
    }

    searchScript = (event) => {
        let hasNext = true;
        let i = 1;
        const eventTargetValue = event.target.value;
        this.setState({
            search: eventTargetValue,
            planetList: []
        })
        while (hasNext) {
            fetch(`https://swapi.co/api/planets/?search=${eventTargetValue}&page=${i}`)
            .then(response => response.json())
            .then(planets => {
                if (planets.next === null) {
                    hasNext = false;
                }
                if (this.state.search === eventTargetValue) {
                    this.setState({
                        planetList: this.state.planetList.push(planets.results)
                    });
                }
            }
            );
            i++
        }
    }


    render() {
        return (
            <div>
                <Fixed>
                    <h1 className="title"> Star Wars Planetarium </h1>
                    <Searchbar onChange={this.searchScript} />
                </Fixed>
                <Content planets={this.state.planetList} />
            </div>
        );
    }
}

export default App;
