import React, { Component } from 'react';
import Topbar from '../Components/Topbar';
import Searchbar from '../Components/Searchbar';
import Fixed from '../Components/Fixed';
import Content from '../Components/Content';
import './App.css'
import debounce from 'debounce'

class App extends Component {
    constructor() {
        super();
        this.state = {
            search: '',
            planetList: []
        }
    }

    // searchScript = (event) => {
    //     fetch(`https://swapi.co/api/planets/?search=${event.target.value}`)
    //         .then(response => response.json())
    //         .then(planets => this.setState({
    //             planetList: planets.results
    //         }));
    // }

    searchScript = (event) => debounce(() => {
        fetch(`https://swapi.co/api/planets/?search=${event.target.value}`) .then(response => response.json()) .then(planets => this.setState({ planetList: planets.results })); },1000);


    render() {
        return (
            <div>
                <Fixed>
                    <Topbar />
                    <Searchbar onChange={this.searchScript} />
                </Fixed>
                <Content planets={this.state.planetList} />
            </div>
        );
    }
}

export default App;
