import React, { Component } from 'react';
import Searchbar from '../Components/Searchbar';
import Fixed from '../Components/Fixed';
import Content from '../Components/Content';
import './App.css'

class App extends Component {
    constructor() {
        super();
        this.state = {
            search: '',
            planetList: []
        }
    }

    searchScript = (event) => {
        const eventTargetValue = event.target.value;
        this.setState({
            search: eventTargetValue
        })
        fetch(`https://swapi.co/api/planets/?search=${eventTargetValue}`)
            .then(response => response.json())
            .then(planets => {
                if (this.state.search === eventTargetValue) {
                    temparray = [];
                    temparray.push(planets.results)
                    this.setState({
                        planetList: temparray
                    });
                }
                console.log(this.state.search);
            }
            );
    }


    render() {
        return (
            <div>
                <Fixed>
                    <h1 className="title"> Star Wars Planetarium </h1>
                    <Searchbar onChange={this.searchScript} />
                </Fixed>
                <Content planets={this.state.planetList} />
            </div>
        );
    }
}

export default App;
