import React, { Component } from 'react';
import Topbar from '../Components/Topbar';
import Searchbar from '../Components/Searchbar';
import Fixed from '../Components/Fixed';
import Content from '../Components/Content';
import './App.css'

class App extends Component {
    constructor() {
        super();
        this.state = {
            search: '',
            planetList: []
        }
    }

    searchScript = (event) => {
        this.setState({ search: event.target.value });
        fetch(`https://swapi.co/api/planets/?search=${event.target.value}`)
            .then(response => response.json())
            .then(planets => this.setState({ planetList: planets.results }));
    }

    render() {
        const [search, planetList] = this.state
        return (
            <div>
                <Fixed>
                    <Topbar />
                    <Searchbar onChange={this.searchScript} />
                </Fixed>
                <Content planets={this.state.planetList} />
            </div>
        );
    }
}

export default App;
